import React, { useEffect, useState } from 'react';
import TransactionForm from './components/TransactionForm';
import TransactionList from './components/TransactionList';
import Summary from './components/Summary';
import { fetchTransactions, createTransaction, deleteTransaction } from './api';

function App() {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  // load transactions on mount
  useEffect(() => {
    const load = async () => {
      setLoading(true);
      const data = await fetchTransactions();
      setTransactions(data || []);
      setLoading(false);
    };
    load();
  }, []);

  const addTransaction = async (trx) => {
    const created = await createTransaction(trx);
    if (created) {
      // add to local state (puts newest at top)
      setTransactions(prev => [created, ...prev]);
    }
  };

  const removeTransaction = async (id) => {
    const res = await deleteTransaction(id);
    if (res) {
      setTransactions(prev => prev.filter(t => t._id !== id));
    }
  };

  return (
    <div className="container">
      <h1>Expense Tracker Pro</h1>

      <div className="top">
        <Summary transactions={transactions} />
        <TransactionForm onAdd={addTransaction} />
      </div>

      <h2>Transactions</h2>
      {loading ? <p>Loading...</p> : <TransactionList transactions={transactions} onDelete={removeTransaction} />}
    </div>
  );
}

export default App;
